import React, { Component } from 'react';
import actions from '../../services/index';
import '../../css/main.css'
import '../../css/noscript.css'


class Profile extends Component {
    state = { 
      profile : {}
    }

    componentDidMount(){
        if(!this.props.user.email){ 
            this.props.history.push('/log-in') 
        } 
        // console.log(this.props.user);
        actions.getuserdata()
        .then(res => {
          this.setState({ profile: res.data.user });
          console.log(this.state.profile)
        }).catch(({ error }) => console.error(error.data));
    }

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value  });
    }

    handleSubmit = async (event) => {
        event.preventDefault();
        let res = await actions.project(this.state)

        console.log(res);        
    }

    displayTeam = (teamName) => {
      let startingTeam1 = this.state.teams[teamName];
      // console.log(this.state.teams[teamName]);
      let wordCloud = {};
      //team1WordCloud
      let score = 0;
      let team1 = startingTeam1.map((player, i) => {
        // console.log(startingTeam1[i].Name);
        let result = sentiment.analyze(
          this.state.horoscopes[player.ZodiacSign].today.horoscope
        );
        let result2 = sentiment.analyze(
          this.state.horoscopes[player.ZodiacSign].week.horoscope
        );
        // console.log(result, result2);
        let playerscore = result.score + result2.score;
        let playerWords = [...result.words, ...result2.words];
        playerWords.forEach((word) => {
          wordCloud[word] ? wordCloud[word]++ : (wordCloud[word] = 1);
        });
        score += playerscore;
        // let hPlayer = this.state.horoscopes[player.ZodiacSign];
        return (
          <div className="card" key={player.Name}>
            <div className="card-inner">
              <div className="card-front">
                <img
                  className="row___picture"
                  src={startingTeam1[i].PlayerPicture}
                  alt={startingTeam1[i].Name}
                ></img>
              </div>
              <div className="card-back" key={player.Name}>
                <ul>
                  <li>
                    <strong>Name:</strong>
                    {startingTeam1[i].Name}
                  </li>
                  <li>
                    <strong>Position:</strong>
                    {startingTeam1[i].Position}
                  </li>
                  <li>
                    <strong>Zodiac Sign:</strong>
                    {startingTeam1[i].ZodiacSign}
                  </li>
                </ul>
              </div>
            </div>
          </div>
        );
      });
      console.log(startingTeam1);

      let cloud = [];

      for (let w in wordCloud) {
        cloud.push(
          React.createElement(
            "li",
            {
              className: "word",
              style: {
                color: "#" + (((1 << 24) * Math.random()) | 0).toString(16),
                fontSize: wordCloud[w] * 10 + "px",
                position: "absolute",
                top: Math.random() * 400 + "px",
                left: Math.random() * 500 + "px",
                transform: "rotate(" + 90 * Math.random() + "deg)",
              },
            },
            w
          )
        );
      }

      //let theCloud = React.createElement("ul", { className: "cloud" }, cloud);

      team1.unshift(
        <div>
          <ul className="wordcloud">{cloud}</ul>
        </div>
      );

      return team1;
    };

    render(){
      console.log(this.state.profile);
      const { email, githublink, linkedinurl, username, about, imageUrl , skills, projects} = this.state.profile;
      // let mail = `mailto:${email}`, proj1 = projects[0], proj2 = projects[1], proj3 = projects[2]
      console.log(projects);
      // return(<div>Profile</div>)
        return (
            <div>
                  {/* Profile
                  Welcome {this.props.user.email} !!!  */}
                  <div id="wrapper" className="divided">

              {/* <!-- One --> */}
                <section className="banner style1 orient-left content-align-left Url-position-right fullscreen onload-image-fade-in onload-content-fade-right">
                  <div className="content">
                    <h1>{username}</h1>
                    <p className="major">{skills}</p>
                    <h3>My Porfolio</h3>
                    <p className="major">A showcase of my projects and abilities. </p>
                    <ul className="actions stacked">
                      <li><a href="#first" className="button big wide smooth-scroll-middle">Get Started</a></li>
                    </ul>
                  </div>
                  <div className="image">
                    <img src= {imageUrl} alt="" />
                  </div>
                </section>

              {/* <!-- Two --> */}
              {/* {
                !projects[0]?.title ?
                      ( <section className="spotlight style1 orient-left content-align-left Url-position-center onscroll-Url-fade-in">
                          <div className="content">
                            <h2>{projects[0].title}</h2>
                            <p>{projects[0].description}</p>
                            <h3>Technologies</h3>
                                <p className="major">{projects[0].technologies_used}</p>
                            <ul className="actions stacked">
                              <li><a href={projects[0].githubrepourl} className="button" target="_blank">View on Github</a></li>
                            </ul>
                          </div>
                        <div className="Url">
                            <img src={projects[0].sitelink} alt="" />
                        </div>
                      </section>
                      )

                      :
                      (
                        <h1> No project </h1>
                      )
              } */}

              {/* <!-- Three --> */}
                {/* <section className="spotlight style1 orient-left content-align-left Url-position-center onscroll-Url-fade-in">
                  <div className="content">
                    <h2>{projects[1].title}</h2>
                    <p>{projects[1].description}</p>
                    <h3>Technologies</h3>
                    <p className="major">{projects[1].technologies_used}</p>
                    <ul className="actions stacked">
                      <li><a href={projects[1].githubrepourl} className="button" target="_blank">View on Github</a></li>
                    </ul>
                  </div>
                  <div className="Url">
                    <img src={projects[1].sitelink} alt="" />
                  </div>
                </section> */}

              {/* <!-- Four --> */}
                {/* <section className="spotlight style1 orient-right content-align-left Url-position-center onscroll-Url-fade-in">
                  <div className="content">
                    <h2>{projects[2].title}</h2>
                    <p>{projects[2].description}</p>
                    <h3>Technologies</h3>
                    <p className="major">{projects[2].technologies_used}</p>
                    <ul className="actions stacked">
                      <li><a href={projects[2].githubrepourl}  className="button" target="_blank">View on Github</a></li>
                    </ul>
                  </div>
                  <div className="Url">
                    <img src={projects[2].sitelink} alt="" />
                  </div>
                </section> */}

              {/* <!-- Five --> */}
                {/* <section className="wrapper style1 align-center">
                  <div className="inner">
                    <h2>About Me</h2>
                    <p>{about}</p>
                  </div>
                </section> */}


              {/* // Footer */}
                {/* <footer className="wrapper style1 align-center">
                  <div className="inner">
                    <ul className="icons">
                      <li><a href={githublink} className="icon brands style2 fa-github" target="_blank"><span className="label">Github</span></a></li>
                      <li><a href={linkedinurl} className="icon brands style2 fa-linkedin-in" target="_blank"><span className="label">LinkedIn</span></a></li>
                      <li><a href={mail} className="icon style2 fa-envelope"><span className="label">Email</span></a></li>
                    </ul>
                  </div>
                </footer> */}
              </div>
            </div>
        );
    }


}

export default Profile;


