import React, { Component } from 'react';
import ListItem from '@material-ui/core/ListItem';
import List from '@material-ui/core/List';
import ListItemText from '@material-ui/core/ListItemText';
import Container from '@material-ui/core/Container';
import Button from '@material-ui/core/Button';
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetail from '@material-ui/core/AccordionDetails';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

import IconButton from '@material-ui/core/IconButton';
import EditIcon from '@material-ui/icons/Edit';


import Grid from '@material-ui/core/Grid';

class Confirm extends Component {

    continue = e => {
        e.preventDefault();
        this.props.nextStep();
    }

    back = e => {
        e.preventDefault();
        this.props.PreviousStep();
    }

    RenderAccordion = ({ summary, details, go }) => {
        return(
            <Accordion>
                <AccordionSummary
                    expandIcon={<ExpandMoreIcon />}
                >{summary}</AccordionSummary>
                <AccordionDetail>
                    <div>
                    { details.map((data, index) => {
                        const objKey = Object.keys(data)[0];
                        const objValue = data[Object.keys(data)[0]];

                        return <ListItemText key={index}>{`${objKey}: ${objValue}`}</ListItemText>

                    }) }
                    <IconButton
                        color="primary"
                        component="span"
                        onClick={() => go(`${summary.toLowerCase()}`)}
                    ><EditIcon /></IconButton>
                    </div>
                </AccordionDetail>
            </Accordion>

        ) 
    }

    render() {

        const { values : {username, about, skills, githublink, imageUrl, linkedinurl, project}} = this.props;
        return (
            <div>
                {/* <List>
                    <ListItem>
                        <ListItemText
                            primary="User Name"
                            secondary= {username}
                        />
                    </ListItem>
                    <ListItem>
                        <ListItemText
                            primary="Short Bio"
                            secondary= {about}
                        />
                    </ListItem>
                    <ListItem>
                        <ListItemText
                            primary="catch phrase"
                            secondary= {skills}
                        />
                    </ListItem>

                    <ListItem>
                        <ListItemText
                            primary="Github"
                            secondary= {githublink}
                        />
                    </ListItem>

                    <ListItem>
                        <ListItemText
                            primary="image"
                            secondary= {imageUrl}
                        />
                    </ListItem>

                    <ListItem>
                        <ListItemText
                            primary="LinkedIn"
                            secondary= {linkedinurl}
                        />
                    </ListItem>

                </List> */}

                <Container maxWidth='sm'>
                <h3>Review</h3>
                <RenderAccordion summary="Your Details" go={ go } details={[
                    { 'Username': username },
                    { 'Your Bio': about },
                    { 'Your Catch Phrase': skills },
                ]} />
                {/* <RenderAccordion summary="Address" go={ go } details={[
                    { 'Address': address },
                    { 'City': city },
                    { 'State': state },
                    { 'Zip': zip },
                ]} /> */}

                </Container>

                <br/>
                <div className="button-div">
                    <button className="btn btn-primary" onClick={this.back}>Return</button>

                    <button className="btn btn-primary" onClick={this.continue}>Continue</button>
                </div>    
            </div>
        );
    }
}

const styles = {
    button: {
        margin: 15
    }
}


export default Confirm;


