import React, { Component } from 'react';
import PersonalDetails from './PersonalDetails';
import Project from './Projects';
import Links from './Links';
import Confirm from './Confirm';
import './user.css'

class UserForm extends Component {
    state ={
        step: 1,
        username: '',
        about: '',
        skills: '',
        githublink: '',
        imageUrl: '',
        linkedinurl:'',
        project: [{ picture: '', title: '', technologies_used: '',description: '', githubrepourl: '', sitelink: '' }]
    }


    nextStep = () => {
        const { step } = this.state;
        this.setState({
            step: step + 1
        });
    }

    PreviousStep = () => {
        const { step } = this.state;
        this.setState({
            step: step - 1
        });
    }

    handleChange = input => e => {
        this.setState({ [input]:  e.target.value });
    }

    render() {
        const { step } = this.state;
        const { username, about, skills, githublink, imageUrl, linkedinurl, project} = this.state;
        const values = {username, about, skills, githublink, imageUrl, linkedinurl, project}

        switch (step) {
            case 1: 
                return (
                    <div className="form">
                            <PersonalDetails 
                                nextStep = {this.nextStep}
                                handleChange={this.handleChange}
                                values={values}
                            />
                    </div>  
                );
            case 2:
                return ( 
                    <div className="form">
                        <div >
                        <Project
                            nextStep = {this.nextStep}
                            PreviousStep = {this.PreviousStep}
                            handleChange={this.handleChange}
                            values={values}
                            index = {0}
                        />  
                        </div>     
                        </div>  
                    )
                    case 3:
                return ( 
                    <div className="form">
                        <Project
                            nextStep = {this.nextStep}
                            PreviousStep = {this.PreviousStep}
                            handleChange={this.handleChange}
                            values={values}
                            index = {1}
                        />       
                        </div>  
                    )
            case 4:
                return ( 
                    <div className="form">
                        <Project
                            nextStep = {this.nextStep}
                            PreviousStep = {this.PreviousStep}
                            handleChange={this.handleChange}
                            values={values}
                            index = {2}
                        />       
                        </div>  
                    )
            case 5:
                return ( <Links
                    nextStep = {this.nextStep}
                    PreviousStep = {this.PreviousStep}
                    handleChange={this.handleChange}
                    values={values}
                /> )
            case 6: 
                return  ( <Confirm
                        nextStep = {this.nextStep}
                        PreviousStep = {this.PreviousStep}
                        values={values}
                    /> )
            default:
                break;
        }

    }
}

export default UserForm;

