import React, { Component } from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import GitHubIcon from '@material-ui/icons/GitHub';
import HttpIcon from '@material-ui/icons/Http';




class Project extends Component {
    continue = e => {
        e.preventDefault();
        this.props.nextStep();
    }

    back = e => {
        e.preventDefault();
        this.props.PreviousStep();
    }

    render() {
        const { values, handleChange } = this.props;
        return (
            <div className="form-container">
                <h1 className="mb-5">Projects</h1>
                <div className="form-group">
                    <label htmlFor="skills">Upload Project picture</label>
                    <input
                                        type="file"
                                        aria-describedby="inputGroupFileAddon"
                                        onChange={this.handleFileUpload}
                                        className="image-upload"
                                    />
                </div>
                <div className="form-group">
                    <label htmlFor="title">Project Title</label>
                    <input type="text" className="form-control" name="name" onChange={handleChange('username')} value={values.username} />
                </div>
                <div className="form-group">
                    <label htmlFor="technologies_used">The Technologies Used</label>
                    <input type="text" className="form-control" name="technologies_used" onChange={handleChange('skills')} value={values.skills} />
                </div>
                <div className="form-group">
                    <label htmlFor="description">Project Description</label>
                    <textarea type="text" rows="4" className="form-control" name="description" onChange={handleChange('about')} value={values.about} />
                </div>
                <div className="form-group">
                    <label htmlFor="githubrepourl"><GitHubIcon /> Project GitHub link</label> 
                    <input type="url" className="form-control" name="githubrepourl" onChange={handleChange('githublink')} value={values.githublink} />
                </div>
                <div className="form-group">
                    <label htmlFor="sitelink"><HttpIcon /> Site's url</label> 
                    <input type="url" className="form-control" name="sitelink" onChange={handleChange('sitelink')} value={values.sitelink} />
                </div>

                <br />

                <div className="text-right">
                    <button className="btn btn-primary" onClick={this.back}>Continue</button>
                </div>

                <div className="text-right">
                    <button className="btn btn-primary" onClick={this.continue}>Continue</button>
                </div>
            </div> 
        );
    }
}




export default Project;

