import React, { Component } from 'react';
import './style.css';
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';
import 'bootstrap';
import { Link } from 'react-router-dom';





class Landingpage extends Component {

    componentDidMount(){

      setInterval(() => {
        let arr = document.querySelectorAll(".background")
        let i 
        arr.forEach((el, index) => {
          if (el.classList[3] === "active"){
            el.classList.remove("active")
            i = index
          }
        })
        if (i === 2){
          arr[0].classList.add("active")
        }
        else{
          if(arr[i+1])arr[i+1].classList.add("active")
        }
      }, 3000);
    }

  render() {
    return (


  <div id="myCarousel" class="carousel carousel-fade slide" data-ride="carousel" data-interval="3000">
    <div class="carousel-inner" role="listbox">
      <div class="item background a active"></div>
      <div class="item background b"></div>
      <div class="item background c"></div>
    </div>


  <div class="covertext">
    <div class="col-lg-10">
      <h1 class="title">Hire-My-Dev</h1>
      <h3 class="subtitle">A site where Junior Devs can connect</h3>
      </div>
      <div class="col-xs-12 explore">
      {
            !this.props.user?.email ?
          ( 
            <Link to="/log-in"><button type="button" class="btn btn-lg explorebtn">Sign-In</button></Link>
          ):(
            <Link to="/profile"><button type="button" class="btn btn-lg explorebtn">View-profile</button></Link>
          ) }
      </div>
    </div>
    <section id="features">
          <div className="contain">
            <div>
                <img src="./images/zero-configuration.svg" alt="zero-configuration" />
                <h4 className="member-name" > Find Jobs</h4>
                <p>Create an org, add your team, and start collaborating. Nothing to configure, set up, or manage.</p>
            </div>
            <div>
                <img src="./images/team-management.svg" alt="team management" />
                <h4 className="member-name" > Team management </h4>
                <p>Control who has access to what modules within your team namespace using straightforward team management capabilities.</p>
            </div>
            <div>
                <img src="./images/familiar-features.svg" alt="familiar features" />
                <h4 className="member-name" > Familiar features </h4>
                <p>npm Orgs has 100% parity with all the public npm registry features your developers already use.</p>
            </div>
            <div>
                <img src="./images/npm-audit.svg" alt="npm-audit" />
                <h4 className="member-name" > npm audit </h4>
                <p> Enjoy the security auditing features built into the npm client, a zero-friction way to make open source software safer.</p>
            </div>
          </div>
          <Link to="/aboutus" className="feature-btn">About us</Link>
      </section> 



    {/* <div class="col-xs-12 explore">
      <button type="button" class="btn btn-lg explorebtn">Sign Up</button>
    </div> */}
  </div>

    );
  }

}

export default Landingpage;

