import Container from '@material-ui/core/Container';
import RenderAccordion from './RenderAccordian';


import Grid from '@material-ui/core/Grid';

class Confirm extends Component {

    continue = e => {
        e.preventDefault();
        this.props.nextStep();
    }

    back = e => {
        e.preventDefault();
        this.props.PreviousStep();
