import React, { Component } from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import GitHubIcon from '@material-ui/icons/GitHub';
import HttpIcon from '@material-ui/icons/Http';




class Project extends Component {
    continue = e => {
        e.preventDefault();
        if(this.props.index < 2){
            this.props.nextStep();
            this.props.addProj();
        }

        this.props.nextStep();

    }

    back = e => {
        e.preventDefault();
        this.props.PreviousStep();
    }

    render() {
        const { values, handleChange, index } = this.props;
        let picId = `picture-${index}`,titleId = `title-${index}`, descId = `description-${index}`, techId = `technologies_used-${index}`, 
        gitId = `githubrepourl-${index}`, siteId = `sitelink-${index}` 
        return (
            <div className="form-container">
                <h1 className="mb-5">Project {index + 1}</h1>
                <div className="form-group">
                    <label htmlFor={picId}>Upload Project picture for your portfolio</label>
                    <input
                                        type="file"
                                        aria-describedby="inputGroupFileAddon"
                                        onChange={this.handleFileUpload}
                                        className="picture"
                                        name={picId}
                                        value={values.project[index].picture}

                                    />
                </div>
                <div className="form-group">
                    <label htmlFor={titleId}>Project Title</label>
                    <input type="text" className="title" name={titleId} onChange={handleChange('title')} value={values.project[index].title} />

                    <label htmlFor={techId}>The Technologies Used</label>
                    <input type="text" className="technologies_used" name={techId} onChange={handleChange('technologies_used')} value={values.project[index].technologies_used} />

                    <label htmlFor={descId}>Project Description</label>
                    <textarea type="text" rows="4" className="description" name={descId} onChange={handleChange('description')} value={values.project[index].description} />

                    <label htmlFor={gitId}><GitHubIcon /> Project GitHub link</label> 
                    <input type="url" className="githubrepourl" name={gitId} onChange={handleChange('githubrepourl')} value={values.project[index].githubrepourl} />

                    <label htmlFor={siteId}><HttpIcon /> Site's url</label> 
                    <input type="url" className="sitelink" name={siteId} onChange={handleChange('sitelink')} value={values.project[index].sitelink} />
                </div>

                <div className="text-left">
                    <button className="btn btn-primary" onClick={this.back}>Return</button>
                </div>

                <div className="text-right">
                    <button className="btn btn-primary" onClick={this.continue}>Continue</button>
                </div>
            </div> 
        );
    }
}




export default Project;

