import React, { Component } from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { GitHubIcon, HttpIcon } from '@material-ui/icons/LinkedIn';
import InputAdornment from '@material-ui/core/InputAdornment';


class Project extends Component {
    continue = e => {
        e.preventDefault();
        this.props.nextStep();
    }

    back = e => {
        e.preventDefault();
        this.props.PreviousStep();
    }

    render() {
        const { values, handleChange } = this.props;
        return (
            <div>
            <form noValidate autoComplete="on">
                <div>
                <Button
                    variant="contained"
                    component="label"
                    >
                    upload project picture
                    <input
                        type="file"
                        style={{ display: "none" }}
                    />
                </Button>
                </div>
                <TextField
                    required
                    // id="outlined-required"
                    label="Full name"
                    defaultValue={values.username}
                    variant="outlined"
                    onChange={handleChange('username')}
                />
                <br />
                <TextField
                    id="standard-multiline-static"
                    label="Short Bio about you"
                    multiline
                    variant="outlined"
                    rows={4}
                    defaultValue={values.about}
                    onChange={handleChange('about')}
                />
                <br/>
                <TextField
                    id="standard-multiline-static"
                    label="your catch phrase"
                    multiline
                    rows={2}
                    defaultValue={values.skills}
                    onChange={handleChange('skills')}
                />
                <br/>
                <br />
                <TextField
                    id="standard-multiline-static"
                    label="Description"
                    multiline
                    variant="outlined"
                    rows={4}
                    defaultValue='description'

                />
                <br/>

                <TextField
                    id="input-with-icon-textfield"
                    label="Github Link"
                    InputProps={{
                    startAdornment: (
                        <InputAdornment position="start">
                            <GitHubIcon />
                        </InputAdornment>
                    ),
                    }}
                    defaultValue='githubrepourl'  
                />
                <TextField
                    id="input-with-icon-textfield"
                    label="Website Link"
                    InputProps={{
                    startAdornment: (
                        <InputAdornment position="start">
                            <HttpIcon />
                        </InputAdornment>
                    ),
                    }}
                    defaultValue='sitelink'

                />
                <div>
                    <Button variant="contained" color="secondary" style={styles.button} onClick={this.back}>
                        Previous
                    </Button>
                    <Button variant="contained" color="primary" style={styles.button} onClick={this.continue}>
                        continue
                    </Button>
                </div>
                <Button variant="contained" color="primary" style={styles.button} onClick={this.continue}>
                    continue
                </Button>
            </form>

            </div>
        );
    }
}

const styles = {
    button: {
        margin: 15
    }
}


export default Project;

